mapper.select:
SELECT orderid, orderqty, orderuse, p.*
FROM orders o, products p
WHERE o.prodid = p.prodid AND orderid like #{memid}

SELECT orderid, orderqty, orderuse, p.*
FROM orders o, products p
WHERE o.prodid = p.prodid AND orderid=#{orderid}

mapper.update:
update orders
set orderuse='YES'
where orderid=#{orderid}
