mapper.insert:
insert into products
values ( productseq.nextval
		, #{prodname}
		, #{prodcateid}
		, #{prodimage}
		, #{prodprice}
		, #{prodstock}
		, sysdate
		, #{prodstartdate}
		, #{prodenddate}
		, #{prodcontent} )

mapper.select:
select rownum, a.* from ( select p.prodid , p.prodname , p.prodcateid , c.prodcatename , p.prodimage , p.prodprice , p.prodstock , to_char(p.prodinputdate,'yyyy-MM-dd') as prodinputdate , to_char(p.prodstartdate,'yyyy-MM-dd') as prodstartdate , to_char(p.prodenddate,'yyyy-MM-dd') as prodenddate , p.prodcontent from products p , prodcategory c where p.prodcateid = c.prodcateid order by p.prodinputdate desc, p.prodid desc) a
where prodname like #{keyword}
where prodcontent like #{keyword}
where prodname || prodcontent like #{keyword}

select count(*) as cnt from products

	where prodname like #{keyword}


	where prodcontent like #{keyword} 


	where prodname || prodcontent like #{keyword}

mapper.update:
update products
	set prodname = #{prodname}
		, prodcateid = #{prodcateid}
		, prodimage = #{prodimage}
		, prodprice = #{prodprice}
		, PRODINPUTDATE = sysdate
		, prodstartdate = #{prodstartdate}
		, PRODENDDATE = #{prodenddate}
		, PRODCONTENT = #{prodcontent}
  where prodid = #{prodid}
